// SPDX-License-Identifier: SimPL-2.0

pragma solidity ^0.8.0;
pragma experimental ABIEncoderV2;

import "./RichHero.sol";

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";
/**·
* @title AirDrop - Romance of the Three Kingdoms AirDrop
*
* Version v1.1.0
*
*/

contract AirDrop2 is Ownable {
    using SafeMath for uint256;

    struct DropBoxStruct {
        uint256 totalHero;
        mapping (uint256 => uint256) rarities;
        mapping (uint256 => uint256) qualities;
    }

    uint256 public constant RAND_BASE = 1000;
    RichHero public HERO;

    bool airDropOpen;
    bytes32 merkleRoot;
    mapping (uint256 => DropBoxStruct) private _dropBoxes;
    mapping (address => uint256) private _whiteList;

    constructor(address nft) {
        HERO = RichHero(nft);
        _dropBoxes[0].rarities[0] = 60;
        _dropBoxes[0].rarities[1] = 850;
        _dropBoxes[0].rarities[2] = 80;
        _dropBoxes[0].rarities[3] = 10;
        _dropBoxes[0].qualities[0] = 800;
        _dropBoxes[0].qualities[1] = 150;
        _dropBoxes[0].qualities[2] = 30;
        _dropBoxes[0].qualities[3] = 20;
        _dropBoxes[0].totalHero = 5000;

        _dropBoxes[1].rarities[0] = 0;
        _dropBoxes[1].rarities[1] = 0;
        _dropBoxes[1].rarities[2] = 0;
        _dropBoxes[1].rarities[3] = 1000;
        _dropBoxes[1].qualities[0] = 800;
        _dropBoxes[1].qualities[1] = 150;
        _dropBoxes[1].qualities[2] = 30;
        _dropBoxes[1].qualities[3] = 20;
        _dropBoxes[1].totalHero = 500;
    }

    function setRoot(bytes32 root) external onlyOwner {
        merkleRoot = root;
    }

    function setAirDropState(bool state) external onlyOwner {
        airDropOpen = state;
    }

    function _random(uint256 value, uint256 index) private view returns (uint256) {
        bytes memory seed = abi.encodePacked(block.timestamp, index, msg.sender);
        return uint256(keccak256(seed)) % value;
    }

    function _choose(uint256 index) private view returns (uint64[] memory) {
        uint64[] memory heroDetail = new uint64[](13);
        uint256 randIndex = index;
        heroDetail[0] = 0; //native
        heroDetail[1] = uint64(_random(uint256(HERO.camps()), randIndex++)); //camp
        {
            uint256 ranRarity = _random(RAND_BASE, randIndex++);
            uint256 rarityTotal;
            for (uint64 i = 0; i < 5; i++) {
                rarityTotal = rarityTotal.add(_dropBoxes[index].rarities[i]);
                if (ranRarity >= rarityTotal) {
                    continue;
                } else {
                    heroDetail[2] = i; //rarity
                    break;
                }
            }
        }
        heroDetail[3] = uint64(_random(uint256(HERO.heroIds(heroDetail[2])), randIndex++)); //heroId
        heroDetail[4] = 0; //level
        heroDetail[5] = 0; //star  
        uint256 ranQuality = _random(RAND_BASE, randIndex++);
        {
            uint256 qualityTotal;
            for (uint64 i = 0; i < 4; i++) {
                qualityTotal = qualityTotal.add(_dropBoxes[index].qualities[i]);
                if (ranQuality >= qualityTotal) {
                    continue;
                } else {
                    heroDetail[6] = i; //quality
                    break;
                }
            }
        }
        (uint64 baseAddition, uint64 additionMod) = HERO.additions(heroDetail[6]);
        heroDetail[7] = baseAddition + uint64(_random(additionMod, randIndex++)); //addition
        (,
        heroDetail[8],
        heroDetail[9],
        heroDetail[10],
        heroDetail[11],
        heroDetail[12]) = HERO._heroInitials(heroDetail[1], heroDetail[2], heroDetail[3]); //preference
        return heroDetail;
    }

    function claimAirDrop(bytes32[] calldata merkleProof) public {
        require(airDropOpen);
        require(msg.sender == tx.origin, "The caller is not an EOA");
        uint256 number = _dropBoxes[0].totalHero;
        require(number >= 1, "Insufficient quantity left");
        require(_verify(merkleProof, merkleRoot));
        uint64[] memory detail = new uint64[](13);
        detail = _choose(0);
        _dropBoxes[0].totalHero = _dropBoxes[0].totalHero.sub(0);
        HERO.mint(msg.sender, detail);
    }

    function claimSpecialAirDrop() public {
        require(msg.sender == tx.origin, "The caller is not an EOA");
        uint256 number = _dropBoxes[1].totalHero;
        require(number >= 1, "Insufficient quantity left");
        require(_whiteList[msg.sender] > 0, "No available airdrop count");
        _whiteList[msg.sender] = _whiteList[msg.sender].sub(1);
        uint64[] memory detail = new uint64[](13);
        detail = _choose(1);
        _dropBoxes[1].totalHero = _dropBoxes[1].totalHero.sub(1);
        HERO.mint(msg.sender, detail);
    }

    function isInWhiteList(address receiver) public view returns (bool) {
        if (_whiteList[receiver] > 0) {
            return true;
        }
        return false;
    }

    function addWhiteList(address[] memory whiteList) public onlyOwner {
        for (uint256 i = 0; i < whiteList.length; i++) {
            _whiteList[whiteList[i]] = _whiteList[whiteList[i]].add(1);
        }
    }

    function setDropBox(uint256 amount, uint256 boxId, bool add) public onlyOwner {
        if (add) {
            _dropBoxes[boxId].totalHero = _dropBoxes[boxId].totalHero.add(amount);
        } else {
            _dropBoxes[boxId].totalHero = _dropBoxes[boxId].totalHero.sub(amount);
        }
    }

    function getDropBoxNumber() public view returns (uint256, uint256) {
        return (_dropBoxes[0].totalHero, _dropBoxes[1].totalHero);
    }

    function setHero(address hero) public onlyOwner {
        HERO = RichHero(hero);
    }

    function _verify(bytes32[] calldata merkleProof, bytes32 root) internal view returns(bool){
        return MerkleProof.verify(
                merkleProof,
                root,
                keccak256(abi.encodePacked(msg.sender))
            );
    }
}

