import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC1155/IERC1155.sol";
import "@openzeppelin/contracts/token/ERC1155/utils/ERC1155Receiver.sol";

import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

/**·
* @title Battle - Romance of the Three Kingdoms Battle
*
*
* Docs: 
*
* Version v1.1.0
*
*/

contract Battle is Ownable, ERC1155Receiver {
    using SafeMath for uint256;

    uint256 public constant PCT_BASE = 1000;
    uint256 public constant RAND_BASE = 1000;

    struct Addition {
        uint256[] buff; // 增益(组合，金卡...) 具体百分比 0:耐力 1:力量 2:智力 3:敏捷 4:精神 5:战斗力
        uint256[] debuff;// 减益 具体百分比 0:耐力 1:力量 2:智力 3:敏捷 4:精神 5:战斗力
    }

    struct PlayerInfo {
        uint8 camp; //阵营
        uint8 road; //路线
        uint8 currentStep; //当前路线据点下标
        uint8 baseSpeed; //基础速度
        uint8 speedBuffType; //速度buff 增益/减益 0:无 1:增益 2:减益
        uint8 additionSpeed; //总额外 提升/减少 速度
        uint8 state; //状态 0:普通状态 1:战斗中状态 2:埋伏状态 3:扎营状态
        uint8 lastEvent;//上个事件序号
        uint8 lastBuffId;//上个道具编号
        uint64[5] times; //0上次移动初始时间,1预计下个据点达到时间,2上次触发随机事件时间,3上次使用道具的时间,4道具到期时间,5抵达决战战场时间
        uint256[] commonTokenIds; //普通卡
        uint256[] goldenTokenIds; //金卡
        Addition addition; //属性加成 （随机事件、战斗、道具、组合）
    }

    struct Node {
        uint8 nodeId; //据点id
        uint8 nodeType; //0:普通点（选项 前进） 1:埋伏点（判断阵营 埋伏方 选项：埋伏（参战），继续前进 被埋伏方 选项：继续前进） 2:埋伏跳跃点（下一个点为埋伏点，该点前进则直接跳跃进入埋伏点，触发埋伏战， 选项（投降，参战）） 3:遭遇点
        uint8[] camps; //camps[0] camps[1] 为交战双方，如果是埋伏点 camps[0]为埋伏方
        uint64 nowConflictStartTime; //据点内当前战斗开始时间
        mapping(uint8 => address[]) inConflictsPlayers; // 阵营内阵营相对应的交战中地址
        mapping(uint8 => address[]) inNodePlayers; // 据点内阵营相对应的地址
    }

    RichHero public HERO; 
    RichShop public RICHSHOP; 
    BattleShop public BATTLESHOP; 
    IERC20 public MMC;

    uint64 public battleStartTime;
    uint64 public battleEndTime = battleStartTime + 7 days;
    uint64 confilctLastTime = 5 hours;
    uint64 surrenderAddition = 999;

    address[][] public arriveAddress;
    uint256[] public arrivePower;

    uint256 public totalBetPool = 100000e18;
    mapping (uint8 => uint256) public campPool;
    mapping (address => mapping(uint8 => uint256)) public betInfos;

    mapping (uint8 => mapping(uint8 => uint8[])) public roadMap; // camp => roadId => roadMap
    mapping (uint8 => mapping(uint8 => uint64[])) public distance; // camp => roadId => distance
    mapping (uint8 => Node) public nodeInfo; //nodeId => nodeInfo;
    mapping (uint8 => address[]) public campPlayers; //camp => addresses;
    mapping (address => PlayerInfo) public players; //address => playerInfo;

    mapping (uint8 => mapping(uint8 => uint64)) public randomEvent; //add/sub => properties => percentage; /0增益 1减益 =》 属性 =》 百分比

    address public feeRecipient = 0x8F3b8f3046dEb7C4E227dC0c6379051755Ba3142;

    constructor(address nft, address richShop, address battleShop, address mmc) {
        HERO = RichHero(nft);
        RICHSHOP = RichShop(richShop);
        BATTLESHOP = BattleShop(battleShop);
        MMC = IERC20(mmc);

        roadMap[0][0] = [7, 8, 5, 6, 24]; 
        distance[0][0] = [100, 0, 300, 600];

        roadMap[0][1] = [7, 9, 10, 11, 12, 24];
        distance[0][1] = [100, 200, 0, 300, 400];

        roadMap[1][0] = [19, 20, 21, 2, 24];
        distance[1][0] = [100, 200, 0, 700];

        roadMap[1][1] = [19, 23, 17, 18, 24];
        distance[1][1] = [100, 0, 200, 700];

        roadMap[2][0] = [0, 1, 2, 6, 24];
        distance[2][0] = [100, 0, 200, 700];

        roadMap[2][1] = [0, 3, 4, 5, 6, 24];
        distance[2][1] = [100, 200, 0, 300, 400];

        roadMap[3][0] = [13, 14, 11, 15, 24];
        distance[3][0] = [100, 0, 200, 700];

        roadMap[3][1] = [13, 16, 17, 18, 24];
        distance[3][1] = [100, 0, 200, 700];

        // 0:普通点（选项 前进） 1:埋伏点（判断阵营 埋伏方 选项：埋伏（参战），继续前进 被埋伏方 选项：继续前进） 2:埋伏跳跃点（下一个点为埋伏点，该点前进则直接跳跃进入埋伏点，触发埋伏战， 选项（投降，参战）） 3:遭遇点
        nodeInfo[0].nodeId = 0; //江柠
        nodeInfo[0].nodeType = 0; //普通点

        nodeInfo[1].nodeId = 1; //松子湾
        nodeInfo[1].nodeType = 2; //埋伏跳跃点
        nodeInfo[1].camps[0] = 1; //蜀国埋伏
        nodeInfo[1].camps[1] = 2; //吴国被埋伏        

        nodeInfo[2].nodeId = 2; //五溪
        nodeInfo[2].nodeType = 1; //埋伏点
        nodeInfo[2].camps[0] = 1; //蜀国埋伏
        nodeInfo[2].camps[1] = 2; //吴国被埋伏

        nodeInfo[3].nodeId = 3; //箱山
        nodeInfo[3].nodeType = 0; //普通点

        nodeInfo[4].nodeId = 4; //锦兰
        nodeInfo[4].nodeType = 2; //埋伏跳跃点
        nodeInfo[4].camps[0] = 2; //吴国埋伏
        nodeInfo[4].camps[1] = 0; //魏国被埋伏

        nodeInfo[5].nodeId = 5; //稻城
        nodeInfo[5].nodeType = 1; //埋伏点
        nodeInfo[5].camps[0] = 2; //吴国埋伏
        nodeInfo[5].camps[1] = 0; //魏国被埋伏

        nodeInfo[6].nodeId = 6; //崇江
        nodeInfo[6].nodeType = 3; //遭遇点
        nodeInfo[6].camps[0] = 0; //吴国
        nodeInfo[6].camps[1] = 1; //魏国

        nodeInfo[7].nodeId = 7; //丰驰
        nodeInfo[7].nodeType = 0;//普通点

        nodeInfo[8].nodeId = 8; //瑞锦
        nodeInfo[8].nodeType = 2;//埋伏跳跃点
        nodeInfo[8].camps[0] = 2; //吴国埋伏
        nodeInfo[8].camps[1] = 0; //魏国被埋伏

        nodeInfo[9].nodeId = 9; //芷陵
        nodeInfo[9].nodeType = 0;//普通点

        nodeInfo[10].nodeId = 10;//白果寺
        nodeInfo[10].nodeType = 2;//埋伏跳跃点
        nodeInfo[10].camps[0] = 0; //魏国埋伏
        nodeInfo[10].camps[1] = 3; //群雄被埋伏

        nodeInfo[11].nodeId = 11;  //白果林
        nodeInfo[11].nodeType = 1; //埋伏点
        nodeInfo[11].camps[0] = 0; //魏国埋伏
        nodeInfo[11].camps[1] = 3; //群雄被埋伏

        nodeInfo[12].nodeId = 12;  //朝驿
        nodeInfo[12].nodeType = 0; //普通点

        nodeInfo[13].nodeId = 13; //襄阴
        nodeInfo[13].nodeType = 0; //普通点

        nodeInfo[14].nodeId = 14; //苏安寺
        nodeInfo[14].nodeType = 2; //埋伏跳跃点
        nodeInfo[14].camps[0] = 0; //魏国埋伏
        nodeInfo[14].camps[1] = 3; //群雄被埋伏

        nodeInfo[15].nodeId = 15; //合川
        nodeInfo[15].nodeType = 0; //普通点

        nodeInfo[16].nodeId = 16; //望江谷
        nodeInfo[16].nodeType = 2; //埋伏跳跃点
        nodeInfo[16].camps[0] = 3; //群雄埋伏
        nodeInfo[16].camps[1] = 1; //蜀国被埋伏

        nodeInfo[17].nodeId = 17; //岭南
        nodeInfo[17].nodeType = 1; //埋伏点
        nodeInfo[17].camps[0] = 3; //群雄埋伏
        nodeInfo[17].camps[1] = 1; //蜀国被埋伏

        nodeInfo[18].nodeId = 18; //雾县
        nodeInfo[18].nodeType = 3; //遭遇战
        nodeInfo[17].camps[0] = 1; //蜀国
        nodeInfo[17].camps[1] = 3; //群雄

        nodeInfo[19].nodeId = 19; //樊城
        nodeInfo[19].nodeType = 0; //普通点

        nodeInfo[20].nodeId = 20; //飞马道
        nodeInfo[20].nodeType = 0; //普通点

        nodeInfo[21].nodeId = 21; //商圩
        nodeInfo[21].nodeType = 2; //埋伏跳跃点
        nodeInfo[21].camps[0] = 1; //蜀国埋伏
        nodeInfo[21].camps[1] = 2; //吴国被埋伏

        nodeInfo[22].nodeId = 22; //峪岭
        nodeInfo[22].nodeType = 0; //普通

        nodeInfo[23].nodeId = 23; //千里湖
        nodeInfo[23].nodeType = 0; //埋伏跳跃点
        nodeInfo[23].camps[0] = 3; //群雄埋伏
        nodeInfo[23].camps[1] = 1; //蜀国被埋伏

        nodeInfo[24].nodeId = 24; //千里湖
        nodeInfo[24].nodeType = 0; //普通点

        // mapping (uint8 => mapping(uint8 => uint64)) public randomEvent; //add/sub => properties => percentage; /0增益 1减益 =》 属性 0 1 2 3 4  =》 百分比 
        randomEvent[0][0] = 50; //全员 增加 50/1000 5%的耐力
        randomEvent[0][1] = 30; //全员 增加 3%的力量
        randomEvent[0][2] = 40; //全员 增加 4%的智慧
        randomEvent[0][3] = 60; //全员 增加 6%的敏捷
        randomEvent[0][4] = 70; //全员 增加 7%的精神
        randomEvent[1][0] = 50; //全员 减少 50/1000 5%的耐力
        randomEvent[1][1] = 30; //全员 减少 3%的力量
        randomEvent[1][2] = 40; //全员 减少 4%的智慧
        randomEvent[1][3] = 60; //全员 减少 6%的敏捷
        randomEvent[1][4] = 70; //全员 减少 7%的精神
    }

    // uint256 balance = IERC1155(SHOP).balanceOf(msg.sender, books[detail.quality]);
    // IERC1155(SHOP).safeTransferFrom(msg.sender, address(this), books[detail.quality], _baseAddition[detail.rarity][detail.star][2], "");

    //开启战役
    function startBattle(uint64 _startTime) public onlyOwner {
        require(battleStartTime == 0, "Already started");
        battleStartTime = _startTime;
    }

    //计算门票
    function ticketAmount(uint256[] memory commonTokenIds, uint256[] memory goldenTokenIds) public view returns(uint256) {
        require(commonTokenIds.length <= 5);
        uint256 power;
        for (uint8 i = 0; i < commonTokenIds.length; i++) {
            RichHero.HeroDetail memory detail = HERO.getHero(commonTokenIds[i]);
            power = power.add(detail.power);
        }
        for (uint8 i = 0; i < goldenTokenIds.length; i++) {
            RichHero.HeroDetail memory detail = HERO.getHero(goldenTokenIds[i]);
            power = power.add(detail.power);
        }
        //realPower / 1000
        return power.div(100000);
    }

    //质押卡牌 进入战场
    function fight(uint8 camp, uint8 road, uint256[] memory commonTokenIds, uint256[] memory goldenTokenIds) public {
        //选择阵营
        require(camp < 4);
        //选择普通卡牌和路线
        require(commonTokenIds.length <= 5 && road < 2);
        //确认之前没有行动
        require(players[msg.sender].baseSpeed == 0);
        //质押卡牌
        uint256 power;
        for (uint8 i = 0; i < commonTokenIds.length; i++) {
            HERO.transferFrom(msg.sender, address(this), commonTokenIds[i]);
            RichHero.HeroDetail memory detail = HERO.getHero(commonTokenIds[i]);
            power = power.add(detail.power);
        }
        //质押金卡
        for (uint8 i = 0; i < goldenTokenIds.length; i++) {
            HERO.transferFrom(msg.sender, address(this), goldenTokenIds[i]);
            RichHero.HeroDetail memory detail = HERO.getHero(goldenTokenIds[i]);
            power = power.add(detail.power);
        }
        // todo 收取门票; 
        players[msg.sender].camp = camp;
        players[msg.sender].road = road;
        players[msg.sender].currentStep = 0;
        players[msg.sender].baseSpeed = 20;
        players[msg.sender].times[0] = uint64(block.timestamp);
        players[msg.sender].times[1] = uint64(block.timestamp) + distance[camp][road][0]*3600/20;
        players[msg.sender].commonTokenIds = commonTokenIds;
        players[msg.sender].goldenTokenIds = goldenTokenIds;
        // todo 计算组合;
        campPlayers[camp].push(msg.sender);
    }

    //进军（决策）0: 前进 1:投降 2:参战（埋伏）3:扎营 4:进入战场
    function march(uint8 decision) public {
        //判断当前是否可以行动
        bool[5] memory tactics = getMarchTactics(msg.sender);
        require(tactics[decision], "Decision is invalid");
        uint8 nextNode = getNextNode(msg.sender);
        //
        // uint8 camp; //阵营
        // uint8 road; //路线
        // uint8 currentStep; //当前据点
        // uint8 baseSpeed; //基础速度
        // uint8 speedBuffType; //速度buff 增益/减益 0:无 1:增益 2:减益
        // uint8 additionSpeed; //总额外 提升/减少 速度
        // uint8 state; //状态 0:普通状态 1:战斗中状态 2:埋伏状态
        // uint8 lastEvent;//上个事件序号
        // uint8 lastBuffId;//上个道具编号
        // uint64[] times; //0上次移动初始时间,1预计下个据点达到时间,2上次触发随机事件时间,3上次使用道具的时间,4道具到期时间,5抵达决战战场时间
        // uint256[] commonTokenIds; //普通卡
        // uint256[] goldenTokenIds; //金卡
        // Addition addition; //属性加成 （随机事件、战斗、道具、组合）
        if (decision == 0) {
            //todo 随机事件
            players[msg.sender].currentStep = players[msg.sender].currentStep + 1;
            players[msg.sender].times[0] = uint64(block.timestamp);
            if(players[msg.sender].times[4] <= block.timestamp) {
                //道具未到期
                //根据道具时间计算下个点到达时间
            } else {
                players[msg.sender].times[1] = uint64(block.timestamp) + distance[players[msg.sender].camp][players[msg.sender].road][players[msg.sender].currentStep]*3600/20;
            }
        }

        // if (previewNextNode(msg.sender)) {

        // }

    }

    //触发战斗 只能在遭遇点使用，聚集当前遭遇点双方进行战斗
    function trigger() public {

    }

    //使用增益道具
    function useBuff(uint8 buffId) public {
        //todo 使用道具 计算增益
    }

    //领取战役奖励和取回质押卡牌
    function claim() public {
        require(block.timestamp >= battleEndTime, "The battle is not over");
        for (uint8 i = 0; i < players[msg.sender].commonTokenIds.length; i++) {
            HERO.transferFrom(address(this), msg.sender, players[msg.sender].commonTokenIds[i]);
        }
        //金卡
        for (uint8 i = 0; i < players[msg.sender].goldenTokenIds.length; i++) {
            HERO.transferFrom(address(this), msg.sender, players[msg.sender].goldenTokenIds[i]);
        }
        //todo 发放战役奖励
    }

    //获取当前可以进行的决策
    function getMarchTactics(address account) public view returns (bool[5] memory tactics) {
        //如果没达到点位，不可行动
        if (block.timestamp < players[account].times[1]) {
            return tactics;
        } 
        uint8 nextNode = getNextNode(account);
        if (nodeInfo[nextNode].nodeId > 24) {
            return tactics;
        }
        //如果到达点位，判断决策
        //进军（决策）0: 前进 1:投降 2:参战（埋伏）3:扎营 4:进入战场
        //普通点 可以前进 0，埋伏点 可以选前进和战斗（继续埋伏状态）0  埋伏跳跃点 埋伏方可以选前进 和 埋伏 0，2 
        //被埋伏方可以选 快速通过（还没有埋伏方埋伏的情况下）投降和参战 0 1 2  遭遇点可以选前进和扎营 鹿原可以选进入战场 
        if (nodeInfo[nextNode].nodeId == 24) {
            //鹿原
            tactics[4] = true;
            return tactics;
        }
        if (nodeInfo[nextNode].nodeType == 0) {
            //普通点
            tactics[0] = true;
            return tactics;
        }
        if (nodeInfo[nextNode].nodeType == 1) {
            //埋伏点
            tactics[0] = true;
            tactics[2] = true;
            return tactics;
        }
        if (nodeInfo[nextNode].nodeType == 2) {
            //埋伏跳跃点
            //判断阵营 埋伏方或被埋伏方
            if (players[account].camp == nodeInfo[nextNode].camps[0]) {
                //埋伏方
                tactics[0] = true;
                tactics[2] = true;
                return tactics;
            } else {
                //被埋伏方
                tactics[0] = true; //如果没有埋伏，可以快速通过
                tactics[1] = true;
                tactics[2] = true;
                return tactics;
            }
        }
        if (nodeInfo[nextNode].nodeType == 3) {
            //遭遇点
            //遭遇点可以选前进和战斗和扎营
            tactics[0] = true;
            tactics[1] = true;
            tactics[2] = true;
            return tactics;
        }
    } 

    //判断前方是否正在进行战斗或埋伏
    function previewNextNode(address player) public view returns (bool) {
        return true;
    } 

    //获得当前节点
    function getCurrentNode(address player) public view returns(uint8) {
        if (players[player].baseSpeed == 0 && players[player].state == 0) {
            return 100;
        }
        uint8 camp = players[player].camp;
        uint8 road = players[player].road;
        uint8 currentStep = players[player].currentStep; 
        if (roadMap[camp][road][currentStep] == 24) {
            return 100;
        }
        if (block.timestamp > players[player].times[1]) {
            return roadMap[camp][road][currentStep+1];
        }
        return roadMap[camp][road][currentStep];
    }

    //获得前方节点
    function getNextNode(address player) public view returns(uint8) {
        if (players[player].baseSpeed == 0) {
            return 100;
        }
        uint8 camp = players[player].camp;
        uint8 road = players[player].road;
        uint8 currentStep = players[player].currentStep; 
        if (roadMap[camp][road][currentStep] == 24) {
            return 100;
        }
        return roadMap[camp][road][currentStep+1];
    }

