    //押注 MMC
    function bet(uint8 camp, uint256 amount) public {
        require(camp < 4);
        require(MMC.transferFrom(msg.sender, address(this), amount));
        require(block.timestamp < battleStartTime + 3 days, "Bet only open in the first three days");
        betInfos[msg.sender][camp] = betInfos[msg.sender][camp].add(amount);
        totalBetPool = totalBetPool.add(amount);
        campPool[camp] = campPool[camp].add(amount);
    }

    //领取赌场奖励
    function claimBet() public {
        require(block.timestamp >= battleEndTime, "The battle is not over");
        uint8 maxPowerCamp;
        uint256 maxPower;
        for (uint8 i = 0; i < 4; i++) {
            if (arrivePower[i] > maxPower) {
                maxPower = arrivePower[i];
                maxPowerCamp = i;
            }
        }
        uint256 betAmount = betInfos[msg.sender][maxPowerCamp];
        require(betAmount > 0, "No bonus");
        uint256 bonus = betAmount.mul(totalBetPool).div(campPool[maxPowerCamp]);
        MMC.transfer(msg.sender, bonus.mul(90).div(100));
        MMC.transfer(feeRecipient, bonus.mul(10).div(100));
    }

    //战役结束后查看可领取额度
    function getClaimBet(address account) public view returns(uint256) {
        require(block.timestamp >= battleEndTime, "The battle is not over");
        uint8 maxPowerCamp;
        uint256 maxPower;
        for (uint8 i = 0; i < 4; i++) {
            if (arrivePower[i] > maxPower) {
                maxPower = arrivePower[i];
                maxPowerCamp = i;
            }
        }
        uint256 betAmount = betInfos[account][maxPowerCamp];
        if (betAmount == 0) {
            return 0;
        }
        uint256 bonus = betAmount.mul(totalBetPool).div(campPool[maxPowerCamp]);
        return bonus.mul(90).div(100);
    }

