    function _random(uint256 _value, uint256 index) private view returns (uint64) {
        bytes memory seed = abi.encodePacked(block.timestamp, index, msg.sender);
        return uint64(uint256(keccak256(seed)) % _value);
    }

    function _choose(uint64[] memory detail, uint64 preference, uint64[] memory properties, bool saveCamp, uint64 camp) private view
        returns (uint64[] memory) {
        uint64[] memory heroDetail = new uint64[](13);
        uint256 randIndex = 0;
        heroDetail[0] = detail[0]; //native
        if (!saveCamp) {
            heroDetail[1] = uint64(_random(uint256(HERO.camps()), randIndex++)); //camp
        } else {
            heroDetail[1] = camp;
        }
        heroDetail[2] = detail[1];//rarity
        heroDetail[3] = uint64(_random(uint256(HERO.heroIds(heroDetail[2])), randIndex++)); //heroId
        heroDetail[4] = detail[2]; //level
        heroDetail[5] = detail[3]; //star  
        heroDetail[6] = detail[4]; //quality
        heroDetail[7] = detail[5]; //addition
        (uint64 newPreference,,,,,) = HERO._heroInitials(heroDetail[1], heroDetail[2], heroDetail[3]); //preference
        (properties[preference], properties[newPreference]) = (properties[newPreference], properties[preference]);
        heroDetail[8] = properties[0];
        heroDetail[9] = properties[1];
        heroDetail[10] = properties[2];
        heroDetail[11] = properties[3];
        heroDetail[12] = properties[4];
        return heroDetail;
    }
