// SPDX-License-Identifier: SimPL-2.0

pragma solidity ^0.8.0;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts/token/ERC1155/ERC1155.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

/**·
* @title BattleShop - Romance of the Three Kingdoms Battle Shop
*
*
* Docs: 
*
* Version v1.1.0
*
*/

contract BattleShop is ERC1155, AccessControl {
    using SafeMath for uint256;

    bytes32 public constant MINT_ROLE = keccak256("MINT");

    uint256 public constant PCT_BASE = 1e18;

    struct ItemDetail {
        uint256 tokenId; // 货物ID
        bytes32 name; // 名称
        uint8 speedAddition; //速度加成
    }

    uint256 public lootBoxPrice = 2000e18;
    uint256 public tokenListLength;
    mapping (uint256 => ItemDetail) public _items;
    address public feeRecipient = 0x8F3b8f3046dEb7C4E227dC0c6379051755Ba3142;
    IERC20 public MMC; 

    constructor(address mmc) ERC1155 ("Rich Battle Shop") {
        MMC = IERC20(mmc);
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _addItem("01", 5); //0
        _addItem("02", 10); //1
        _addItem("03", 15); //2
        _addItem("04", 20); //3
        _addItem("05", 25); //4
    }

    function addItem(bytes32 name, uint8 speedAddition) public onlyRole(MINT_ROLE) returns (uint256) {
        return _addItem(name, speedAddition);
    }

    function _addItem(bytes32 name, uint8 speedAddition) internal returns (uint256) {
        uint256 tokenId = tokenListLength++;
        _items[tokenId] = ItemDetail(
            tokenId, 
            name,
            speedAddition
        );
        return tokenId;
    }

    // function setItem(uint256 tokenId, uint8 priceToken, uint256 price) public onlyRole(MINT_ROLE) {
    //     _items[tokenId].price = price.mul(PCT_BASE);
    //     _items[tokenId].priceToken = priceToken;
    // }

    function mint(
        address account, uint256 tokenId, uint256 amount
    ) public onlyRole(MINT_ROLE) {
        _mint(account, tokenId, amount, "");
    }

    function buy(
        uint256 amount
    ) public onlyRole(MINT_ROLE) {
        require(msg.sender == tx.origin, "NOT EOA");
        bool s = MMC.transferFrom(msg.sender, feeRecipient, lootBoxPrice.mul(amount));
        require(s, "Transfer Invalid");
        for (uint256 i = 0; i < amount; i++) {
            uint8 tokenId = uint8(_random(5, i));
            _mint(msg.sender, tokenId, 1, "");
        }
    }

    function setPrice(
        uint256 price
    ) public onlyRole(MINT_ROLE) {
        lootBoxPrice = price * 1e18;
    }

    function _random(uint256 _value, uint256 index) private view returns (uint64) {
        bytes memory seed = abi.encodePacked(block.timestamp, index, msg.sender);
        return uint64(uint256(keccak256(seed)) % _value);
    }

    function getAllItems() public view returns (ItemDetail[] memory) {
        uint256 len = tokenListLength;
        ItemDetail[] memory list = new ItemDetail[](len);
        for (uint256 i = 0; i < len; i++) {
            list[i] = _items[i];
        }
        return list;
    }

    function ownList(address account) public view returns (uint256[] memory) {
        uint256 len = tokenListLength;
        uint256[] memory list = new uint256[](len);
        for (uint256 i = 0; i < len; i++) {
            list[i] = balanceOf(account, i);
        }
        return list;
    }

    function supportsInterface(bytes4 interfaceId) public view virtual
        override(ERC1155, AccessControl) returns (bool) {
        return super.supportsInterface(interfaceId);
    }
}
