// SPDX-License-Identifier: SimPL-2.0

pragma solidity ^0.8.0;
pragma experimental ABIEncoderV2;

import "./Battle.sol";

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

/**·
* @title Battle - Romance of the Three Kingdoms Battle
*
*
* Docs: 
*
* Version v1.1.0
*
*/

contract Bet is Ownable {
    using SafeMath for uint256;

    uint256 public constant PCT_BASE = 1000;

    Battle BATTLE;
    IERC20 MMC; 

    uint256 public initBetPool = 100000e18;
    uint256 public totalBetPool = 100000e18;
    mapping (uint8 => uint256) public campPool;
    mapping (address => mapping(uint8 => uint256)) public betInfos;

    address feeRecipient = 0x8F3b8f3046dEb7C4E227dC0c6379051755Ba3142;

    modifier battleOpen() {
        require(BATTLE.battleStartTime() != 0 && block.timestamp >= BATTLE.battleStartTime() && block.timestamp <= BATTLE.battleEndTime(), "The battle is not open");
        _;
    }

    modifier battleClose() {
        require(block.timestamp > BATTLE.battleEndTime(), "The battle is not over");
        _;
    }

    constructor(address battle, address mmc) {
        BATTLE = Battle(battle);
        MMC = IERC20(mmc);
    }

    //--------赌场相关-------    
    //获得赔率
    function getOdds() public view returns (uint256[4] memory odds) {
        odds[0] = (initBetPool + campPool[1] + campPool[2] + campPool[3]).mul(100).div(campPool[0].add(1e18));
        odds[1] = (initBetPool + campPool[0] + campPool[2] + campPool[3]).mul(100).div(campPool[1].add(1e18));
        odds[2] = (initBetPool + campPool[0] + campPool[1] + campPool[3]).mul(100).div(campPool[2].add(1e18));
        odds[3] = (initBetPool + campPool[0] + campPool[1] + campPool[2]).mul(100).div(campPool[3].add(1e18));
    }

    //押注 MMC
    function bet(uint8 camp, uint256 amount) public {
        require(camp < 4);
        require(MMC.transferFrom(msg.sender, address(this), amount));
        require(block.timestamp < BATTLE.battleStartTime() + 3 days, "Bet only open in the first three days");
        betInfos[msg.sender][camp] = betInfos[msg.sender][camp].add(amount);
        totalBetPool = totalBetPool.add(amount);
        campPool[camp] = campPool[camp].add(amount);
    }

    //领取赌场奖励
    function claimBet() public battleClose {
        (, uint8[4] memory campRank) = BATTLE.getCampArrivePower();
        uint256 betAmount = betInfos[msg.sender][campRank[0]];
        require(betAmount > 0, "No bonus");
        uint256 bonus = betAmount.mul(totalBetPool).div(campPool[campRank[0]]);
        MMC.transfer(msg.sender, bonus.mul(90).div(100));
        MMC.transfer(feeRecipient, bonus.mul(10).div(100));
    }

    //战役结束后计算可领取额度
    function getClaimBet(address account) public view returns(uint256) {
        require(block.timestamp >= BATTLE.battleEndTime(), "The battle is not over");
        uint8 maxPowerCamp;
        uint256 maxPower;
        for (uint8 i = 0; i < 4; i++) {
            if (BATTLE.arrivePower(i) > maxPower) {
                maxPower = BATTLE.arrivePower(i);
                maxPowerCamp = i;
            }
        }
        uint256 betAmount = betInfos[account][maxPowerCamp];
        if (betAmount == 0) {
            return 0;
        }
        uint256 bonus = betAmount.mul(totalBetPool).div(campPool[maxPowerCamp]);
        return bonus.mul(90).div(100);
    }
}
