// SPDX-License-Identifier: SimPL-2.0

pragma solidity ^0.8.0;
pragma experimental ABIEncoderV2;

import "./RichHero.sol";

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

/**
* @title Invite - Romance of the Three Kingdoms Invite
*
* Version v1.1.0
*
*/

contract Invite is Ownable {
    using SafeMath for uint256;

    uint256 public PCT_BASE = 10000;
    RichHero public HERO;
    IERC20 public MMC;
    uint256[] public award = [500, 300, 150, 50];
    uint256 public awardLength = 4;

    mapping (address => address) public superior;

    event NewInvite(address player, address inviter);

    function setInviter(address player, address inviter) public {
        require(player != inviter);
        if (superior[player] == address(0)) {
            superior[player] = inviter;
            emit NewInvite(player, inviter);
        }
    }

    function setReward(uint256[] memory reward) public onlyOwner {
        award = reward;
        awardLength = reward.length;
    }
}
