// SPDX-License-Identifier: SimPL-2.0

pragma solidity ^0.8.0;
pragma experimental ABIEncoderV2;

import "./RichHero.sol";
import "./Invite.sol";

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import "@openzeppelin/contracts/utils/Address.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

/**·
* @title Recruit - Romance of the Three Kingdoms Recruit
*
* Version v1.1.0
*
*/

contract Recruit is Ownable {
    using SafeMath for uint256;
    using EnumerableSet for EnumerableSet.UintSet;

    uint256 public constant PCT_BASE = 1e18;
    uint256 public constant RAND_BASE = 1000;

    struct BlindBoxStruct {
        uint256 price;
        uint256 totalHero;
        mapping (uint256 => uint256) rarities;
        mapping (uint256 => uint256) qualities;
    }

    RichHero public HERO;
    IERC20 public MMC;
    address public feeRecipient = 0x8F3b8f3046dEb7C4E227dC0c6379051755Ba3142;
    address public promotionWallet = 0x8F3b8f3046dEb7C4E227dC0c6379051755Ba3142;
    Invite public inviterContract;
    EnumerableSet.UintSet private _tokenIds;

    mapping (address => bool) public buyer;
    mapping (uint256 => BlindBoxStruct) private _blindBoxes;

    event InviterBuy(address buyer, address inviter, uint256 generation, uint256 amount);

    constructor(address token, address nft, address _inviteContract) {
        require(Address.isContract(token), "Token Invalid");
        require(Address.isContract(nft), "Nft Invalid");
        inviterContract = Invite(_inviteContract);
        MMC = IERC20(token);
        HERO = RichHero(nft);

        _blindBoxes[0].price = 5000e18;
        _blindBoxes[0].rarities[0] = 600;
        _blindBoxes[0].rarities[1] = 300;
        _blindBoxes[0].rarities[2] = 99;
        _blindBoxes[0].rarities[3] = 1;
        _blindBoxes[0].qualities[0] = 800;
        _blindBoxes[0].qualities[1] = 150;
        _blindBoxes[0].qualities[2] = 30;
        _blindBoxes[0].qualities[3] = 20;
        _blindBoxes[0].totalHero = 5000;

        _blindBoxes[1].price = 10000e18;
        _blindBoxes[1].rarities[0] = 100;
        _blindBoxes[1].rarities[1] = 500;
        _blindBoxes[1].rarities[2] = 350;
        _blindBoxes[1].rarities[3] = 50;
        _blindBoxes[1].qualities[0] = 500;
        _blindBoxes[1].qualities[1] = 450;
        _blindBoxes[1].qualities[2] = 30;
        _blindBoxes[1].qualities[3] = 20;
        _blindBoxes[1].totalHero = 5000;

        _blindBoxes[2].price = 30000e18;
        _blindBoxes[2].rarities[0] = 20;
        _blindBoxes[2].rarities[1] = 50;
        _blindBoxes[2].rarities[2] = 500;
        _blindBoxes[2].rarities[3] = 420;
        _blindBoxes[2].rarities[4] = 10;
        _blindBoxes[2].qualities[0] = 250;
        _blindBoxes[2].qualities[1] = 400;
        _blindBoxes[2].qualities[2] = 300;
        _blindBoxes[2].qualities[3] = 50;
        _blindBoxes[2].totalHero = 5000;
    }

    function _random(uint256 _value, uint256 index) private view returns (uint64) {
        bytes memory seed = abi.encodePacked(block.timestamp, index, msg.sender);
        return uint64(uint256(keccak256(seed)) % _value);
    }

    function _choose(uint256 blindBox, uint256 index) private view
        returns (uint64[] memory) {
        uint64[] memory heroDetail = new uint64[](13);
        uint256 randIndex = index;
        heroDetail[0] = 0; //native
        heroDetail[1] = uint64(_random(uint256(HERO.camps()), randIndex++)); //camp
        {
            uint256 ranRarity = _random(RAND_BASE, randIndex++);
            uint256 rarityTotal;
            for (uint64 i = 0; i < 5; i++) {
                rarityTotal = rarityTotal.add(_blindBoxes[blindBox].rarities[i]);
                if (ranRarity >= rarityTotal) {
                    continue;
                } else {
                    heroDetail[2] = i; //rarity
                    break;
                }
            }
        }
        heroDetail[3] = uint64(_random(uint256(HERO.heroIds(heroDetail[2])), randIndex++)); //heroId
        heroDetail[4] = 0; //level
        heroDetail[5] = 0; //star  
        uint256 ranQuality = _random(RAND_BASE, randIndex++);
        {
            uint256 qualityTotal;
            for (uint64 i = 0; i < 4; i++) {
                qualityTotal = qualityTotal.add(_blindBoxes[blindBox].qualities[i]);
                if (ranQuality >= qualityTotal) {
                    continue;
                } else {
                    heroDetail[6] = i; //quality
                    break;
                }
            }
        }
        (uint64 baseAddition, uint64 additionMod) = HERO.additions(heroDetail[6]);
        heroDetail[7] = baseAddition + uint64(_random(additionMod, randIndex++)); //addition
        (,
        heroDetail[8],
        heroDetail[9],
        heroDetail[10],
        heroDetail[11],
        heroDetail[12]) = HERO._heroInitials(heroDetail[1], heroDetail[2], heroDetail[3]); //preference
        return heroDetail;
    }

    function buy(uint256 blindBox, uint256 amount, address inviter) public {
        require(msg.sender == tx.origin, "The caller is not an EOA");
        if (!buyer[msg.sender]) {
            buyer[msg.sender] = true;
        }
        inviterContract.setInviter(msg.sender, inviter);
        uint256 salePrice = _blindBoxes[blindBox].price.mul(amount);
        uint256 base = inviterContract.PCT_BASE();
        address previous = msg.sender;
        uint256 total;
        uint256 inviteFee = salePrice.div(10);
        for (uint256 i = 0; i < inviterContract.awardLength(); i++) {
            previous = inviterContract.superior(previous);
            if (previous != address(0)) {
                uint256 award = inviterContract.award(i);
                total += award;
                bool s1 = MMC.transferFrom(msg.sender, previous, inviteFee.mul(award.div(base)));
                require(s1, "Transfer Invalid");
                emit InviterBuy(msg.sender, previous, i+1, inviteFee.mul(award.div(base)));
            } else {
                break;
            }
        }
        // not enough generation, send to promotionWallet
        if (total < base/10) {
            bool s2 = MMC.transferFrom(msg.sender, promotionWallet, inviteFee.mul(base.div(10).sub(total)).div(base));
            require(s2, "Transfer Invalid");
        }

        // buy
        bool success = MMC.transferFrom(msg.sender, feeRecipient, salePrice.mul(90).div(100));
        require(success, "Transfer Invalid");
        uint256 number = _blindBoxes[blindBox].totalHero;
        require(number >= amount, "Insufficient quantity left");

        for (uint256 i = 0; i < amount; i++) {
            uint64[] memory detail = new uint64[](13);
            detail = _choose(blindBox, i);
            _blindBoxes[blindBox].totalHero = _blindBoxes[blindBox].totalHero.sub(1);
            HERO.mint(msg.sender, detail);
        }
    }

    function setBlindBox(uint256 boxIndex, uint256 amount, bool add) public onlyOwner {
        if (add) {
            _blindBoxes[boxIndex].totalHero = _blindBoxes[boxIndex].totalHero.add(amount);
        } else {
            _blindBoxes[boxIndex].totalHero = _blindBoxes[boxIndex].totalHero.sub(amount);
        }
    }

    function getBlindBoxNumber(uint256 boxIndex) public view returns (uint256) {
        return _blindBoxes[boxIndex].totalHero;
    }

    function setBlindBoxPrice(uint256 boxIndex, uint256 price) public onlyOwner {
        _blindBoxes[boxIndex].price = price.mul(PCT_BASE);
    }

    function getBlindBoxPrice() public view returns(uint256[3] memory) {
        uint256[3] memory prices = [_blindBoxes[0].price, _blindBoxes[1].price, _blindBoxes[2].price];
        return prices;
    }

    function setFeeRecipient(address newRecipient) public onlyOwner {
        feeRecipient = newRecipient;
    }

    function setPromotionWallet(address newWallet) public onlyOwner {
        promotionWallet = newWallet;
    }

    function setInvite(address inviter) public onlyOwner {
        inviterContract = Invite(inviter);
    }
}

