// // SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./IRichHero.sol";
import "./Invite.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

// Farm distributes the ERC20 rewards based on staked heroPower to each user.
//
// Cloned from https://github.com/SashimiProject/sashimiswap/blob/master/contracts/MasterChef.sol
// Modified by ERC20 to work for non-mintable ERC20.
contract HeroFarm is Ownable {
    // Info of each user.
    struct UserInfo {
        uint256 goldens;
        uint256 realPower;
        uint256 buffedPower; // How many power the user has provided.
        uint256 rewardDebt; // Reward debt. See explanation below.
        uint256 mdao; //Deposited mdao
        uint256[] tokenIds; //Deposited hero
        uint256 startTime; //Start time
        uint256 endTime; //End time
        uint256 difficulty;
        uint256 pendingAmount;
        uint256 lastLockId;
        bool inFarm;
        bool isUnClaim;
        uint256 exitPercent;
        uint256 inviteReward;
    }

    struct LockInfo {
        uint256 claimStartTime;
        uint256 rewardAmount;
        uint256 claimedAmount;
    }

    // Info of lp pool.
    struct PoolInfo {
        uint256 lastRewardBlock;    // Last block number that ERC20s distribution occurs.
        uint256 accPowerPerShare;   // Accumulated ERC20s per share, times 1e36.
    }

    // Address of the ERC20 Token contract.
    IERC20 public erc20;
    // The total amount of ERC20 that's paid out as reward.
    uint256 public paidOut = 0;
    // ERC20 tokens rewarded per block.
    uint256 public rewardPerBlock;

    // Info of pool.
    PoolInfo public poolInfo;
    // Info of each user.
    mapping (address => UserInfo) public userInfo;
    mapping (address => mapping (uint256 => LockInfo)) public lockInfo;
    // Staking Times
    uint64[] difficultyTime = [7, 14, 30]; //0, 1, 2
    // Staking Addition
    uint64[] difficultyAddition = [1, 3, 5]; //1%, 3%, 5%
    // Total Power
    uint256 public totalAllocPoint = 0;
    // The block number when farming starts.
    uint256 public startBlock;
    // The block number when farming ends.
    uint256 public endBlock;
    uint256 public totalPowers;
    IRichHero public hero;
    Invite public inviterContract;
    uint256[] public form1InviteAward = [6, 3, 1];
    uint256[] public form2InviteAward = [30, 15, 5];
    event Deposit(address indexed user, uint256 amount);
    event Withdraw(address indexed user, uint256 amount);
    event EmergencyWithdraw(address indexed user, uint256 amount);
    event InviterClaim(address buyer, address inviter, uint256 generation, uint256 amount);

    constructor(IRichHero _hero, address _invite, IERC20 _erc20, uint256 _rewardPerBlock, uint256 _startBlock) {
        hero = _hero;
        erc20 = _erc20;
        inviterContract = Invite(_invite);
        rewardPerBlock = _rewardPerBlock;
        startBlock = _startBlock;
        endBlock = _startBlock + 7776000;
        poolInfo = PoolInfo({
            lastRewardBlock: block.number,
            accPowerPerShare: 0
        });
    }

    // View function to see pending ERC20s for a user.
    function pending(address _user) external view returns (uint256) {
        PoolInfo storage pool = poolInfo;
        UserInfo storage user = userInfo[_user];
        uint256 accPowerPerShare = pool.accPowerPerShare;
        if (block.number > pool.lastRewardBlock && totalPowers != 0) {
            uint256 lastBlock = block.number < endBlock ? block.number : endBlock;

            uint256 erc20Rewards = computerReward(pool.lastRewardBlock, lastBlock);
            accPowerPerShare = accPowerPerShare + (erc20Rewards * 1e20) / (totalPowers);
        }
        return user.buffedPower * (accPowerPerShare) / (1e20) - (user.rewardDebt);
    }

    function emergencyWithdraw(address token) public onlyOwner {
        IERC20(token).transfer(owner(), IERC20(token).balanceOf(address(this)));
    }

    // View function for total reward the farm has yet to pay out.
    function totalPending() external view returns (uint256) {
        if (block.number <= startBlock) {
            return 0;
        }
        uint256 lastBlock = block.number < endBlock ? block.number : endBlock;
        return rewardPerBlock * (lastBlock - startBlock) - paidOut;
    }

    // Update reward variables of the given pool to be up-to-date.
    function updatePool() public {
        PoolInfo storage pool = poolInfo;
        uint256 lastBlock = block.number < endBlock ? block.number : endBlock;

        if (lastBlock <= pool.lastRewardBlock) {
            return;
        }
        if (totalPowers == 0) {
            pool.lastRewardBlock = lastBlock;
            return;
        }

        uint256 erc20Rewards = computerReward(pool.lastRewardBlock, lastBlock);
        pool.accPowerPerShare = pool.accPowerPerShare + (erc20Rewards * (1e20) / (totalPowers));
        pool.lastRewardBlock = block.number;
    }

    // Deposit hero power to Farm for ERC20 allocation.
    function deposit(uint256[] memory _tokenIds, uint8 difficulty) public {
        PoolInfo storage pool = poolInfo;
        UserInfo storage user = userInfo[msg.sender];
        require(!user.isUnClaim);
        if (user.inFarm) {
            require(difficulty == user.difficulty);
            require(user.startTime + difficultyTime[user.difficulty] < block.timestamp, "Expired");
        } else {
            require(difficulty < 3);
            user.inFarm = true;
            user.difficulty = difficulty;
            user.startTime = block.timestamp;
            user.endTime = user.startTime + difficultyTime[user.difficulty] * 86400;
        }
        uint256 power;
        uint256 totalGolden;
        for(uint256 i = 0; i < _tokenIds.length; i++) {
            IRichHero.HeroDetail memory detail = hero.getHero(_tokenIds[i]);
            power += detail.power;
            hero.transferFrom(msg.sender, address(this), _tokenIds[i]);
            if (detail.rarity == 4) {
                totalGolden++;
            }
            user.tokenIds.push(_tokenIds[i]);
        }
        user.realPower += power;
        user.goldens += totalGolden;
        uint256 buffedPower = user.realPower * (100 + user.goldens * 10) / 100;
        user.buffedPower = buffedPower;
        updatePool();
        totalPowers += buffedPower;
        if (user.buffedPower > 0) {
            uint256 pendingAmount = user.buffedPower * (pool.accPowerPerShare) / (1e20) - (user.rewardDebt);
            // erc20Transfer(msg.sender, pendingAmount);
            user.pendingAmount += pendingAmount;
        }
        user.rewardDebt = user.buffedPower * (pool.accPowerPerShare) / (1e20);
        emit Deposit(msg.sender, power);
    }

    function depositMdao() public {
        PoolInfo storage pool = poolInfo;
        UserInfo storage user = userInfo[msg.sender];
        require(user.inFarm);
        require(user.mdao < user.realPower * 1e15);
        erc20.transferFrom(msg.sender, address(this), user.realPower * 1e15 - user.mdao);
        updatePool();
        uint256 buffedPower = user.realPower * (100 + user.goldens * 10 + 10) / 100;
        if (user.buffedPower > 0) {
            uint256 pendingAmount = user.buffedPower * (pool.accPowerPerShare) / (1e20) - (user.rewardDebt);
            // erc20Transfer(msg.sender, pendingAmount);
            user.pendingAmount += pendingAmount;
        }
        user.rewardDebt = user.buffedPower * (pool.accPowerPerShare) / (1e20);
        totalPowers -= user.buffedPower;
        user.buffedPower = buffedPower;
        totalPowers += buffedPower;
        user.mdao = user.realPower * 1e15;
    }

    function canDepositMdao(address _user) public view returns (uint256) {
        UserInfo storage user = userInfo[_user];
        return (user.realPower * 1e15 - user.mdao);
    }

    // Claim Heros tokens from Farm.
    function claimHero() public {
        PoolInfo storage pool = poolInfo;
        UserInfo storage user = userInfo[msg.sender];
        require(user.inFarm, "withdraw: can't withdraw more than deposit");
        updatePool();
        totalPowers -= user.buffedPower;
        uint256 pendingAmount = user.buffedPower * pool.accPowerPerShare / (1e20) - (user.rewardDebt);
        // erc20Transfer(msg.sender, pendingAmount);
        user.pendingAmount += pendingAmount;
        user.rewardDebt = user.buffedPower * (pool.accPowerPerShare) / (1e20);

        for (uint256 i = 0; i < user.tokenIds.length; i++) {
            hero.transferFrom(address(this), address(msg.sender), user.tokenIds[i]);
        }
        if (block.timestamp >= user.endTime) {
            user.exitPercent = 100;
        } else {
            uint256 day = (block.timestamp - user.startTime) / 1 days;
            require(day >= 1);
            if (user.difficulty == 0) {
                //第一天提前取出扣70%，第二天提前取出60%
                user.exitPercent = 30 + 10 * day;
            } else if (user.difficulty == 1) {
                //1—4天提前取出扣70%，5--8提前取出40%，9-13天20%
                if (1 < day && day <= 4){
                    user.exitPercent = 30;
                } else if (4 < day && day <= 8) {
                    user.exitPercent = 60;
                } else if (8 < day && day <= 13) {
                    user.exitPercent = 80;
                }
            } else if (user.difficulty == 2) {
                //1—10天提前取出扣70%，11--20提前取出40%，21-29天20%
                if (1 < day && day <= 10){
                    user.exitPercent = 30;
                } else if (10 < day && day <= 20) {
                    user.exitPercent = 60;
                } else if (20 < day && day <= 29) {
                    user.exitPercent = 80;
                }
            }
        }
        uint256 amount = user.mdao;
        user.mdao = 0;
        erc20.transfer(msg.sender, amount);
        user.tokenIds = new uint256[](0);
        user.realPower = 0;
        user.buffedPower = 0;
        user.inFarm = false;
        user.isUnClaim = true;
    }

    //todo:回流
    // Withdraw Heros tokens from Farm.
    function decideClaimForm(bool isLockClaim) public {
        UserInfo storage user = userInfo[msg.sender];
        require(user.isUnClaim);
        user.isUnClaim = false;
        if (!isLockClaim) {
            //直接领取 扣除20税点
            erc20Transfer(msg.sender, user.pendingAmount * user.exitPercent / 100 * 80 / 100);
            //邀请奖励
            address previous = msg.sender;
            for (uint256 i = 0; i < inviterContract.awardLength(); i++) {
                previous = inviterContract.superior(previous);
                if (previous != address(0)) {
                    uint256 award = form1InviteAward[i];
                    uint256 amount = user.pendingAmount * user.exitPercent / 100 * award / 1000;
                    UserInfo storage preUser = userInfo[previous];
                    preUser.inviteReward += amount;
                    erc20Transfer(previous, amount);
                    emit InviterClaim(msg.sender, previous, i+1, amount);
                } else {
                    break;
                }
            }
        } else {
            lockInfo[msg.sender][user.lastLockId].claimStartTime = block.timestamp;
            lockInfo[msg.sender][user.lastLockId].rewardAmount = user.pendingAmount * user.exitPercent / 100 * 115 / 100;    
            user.lastLockId++;
        }
        user.pendingAmount = 0;
        user.rewardDebt = 0;
    }

    function getUserInfo(address user) public view returns(UserInfo memory) {
        return userInfo[user];
    }

    // Withdraw Heros tokens from Farm.
    function lockRewardWithdraw(uint256 lockId) public {
        LockInfo storage lock = lockInfo[msg.sender][lockId];
        require(lock.rewardAmount > 0);
        uint256 timeClaimReward = (block.timestamp - lock.claimStartTime) * lock.rewardAmount / 30 days;
        if (timeClaimReward > lock.rewardAmount) {
            timeClaimReward = lock.rewardAmount;
        }
        uint256 canClaimReward = timeClaimReward - lock.claimedAmount;
        erc20Transfer(msg.sender, canClaimReward * 95 / 100);
        lock.claimedAmount += canClaimReward;
        //invite
        address previous = msg.sender;
        for (uint256 i = 0; i < inviterContract.awardLength(); i++) {
            previous = inviterContract.superior(previous);
            if (previous != address(0)) {
                uint256 award = form2InviteAward[i];
                uint256 amount = canClaimReward * award / 1000;
                UserInfo storage preUser = userInfo[previous];
                preUser.inviteReward += amount;
                erc20Transfer(previous, amount);
                emit InviterClaim(msg.sender, previous, i+1, amount);
            } else {
                break;
            }
        }
    }

    // Transfer ERC20 and update the required ERC20 to payout all rewards
    function erc20Transfer(address _to, uint256 _amount) internal {
        erc20.transfer(_to, _amount);
        paidOut += _amount;
    }

    // Compute Reward from block to block 
    function computerReward(uint256 _fromBlock, uint256 _toBlock) private view returns (uint256) {
        uint256 nrOfBlocks = _toBlock - _fromBlock;
        uint256 erc20Reward = nrOfBlocks * rewardPerBlock;
        return erc20Reward;
    }

    function lockInfos(address user) public view returns(LockInfo[] memory locks) {
        for (uint256 i = 0; i < userInfo[user].lastLockId; i++) {
            locks[i] = lockInfo[user][i];
        }
    }

    // Compute apy of pool
    function APYPercent(uint256 power) external view returns (uint256 apy) {
        if (totalPowers == 0) {
            return 10000;
        }
        if (power == 0) {
            power = 1000000000000000000;
        }
        apy = power * (rewardPerBlock) * (7776000) / (totalPowers) / 1000;
        return apy;
    }
