// SPDX-License-Identifier: SimPL-2.0

pragma solidity ^0.8.0;
pragma experimental ABIEncoderV2;

import "./IRichHero.sol";

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

import "@openzeppelin/contracts/utils/math/SafeMath.sol";

/**·
* @title Staking - Romance of the Three Kingdoms Staking
*
*
* Docs: 
*
* Version v1.1.0
*
*/

contract Staking is Ownable { 
    using SafeMath for uint256; 

    IRichHero HERO; 
    IERC20 MMC; 
    uint256 constant MMC_BASE = 1e18;
    uint256 constant LV_BASE = 1000000;
    uint256 constant PCT_BASE = 1000;
    uint256 constant LV_BONUS_BASE = 1000;

    struct StakingInfo { 
        uint8 stakingDifficulty; 
        uint64 stakingStartTime; 
        uint64 stakingEndTime; 
        uint256 power;
        uint256[] stakingHeros; 
    } 

    struct PlayerInfo { 
        bool inStaking;
        uint8 level;
        uint256 percent;
        uint256 totalReward;
        uint256 canClaimReward;
        StakingInfo stakingInfo;
    }

    uint8 totalLevel = 10;
    uint64[] difficultyTime = [7, 15, 30]; //0, 1, 2
    uint64[] difficultySpeed = [1000, 900, 800];
    uint64 exitPct = 500;

    mapping (uint8 => mapping(uint8 => uint8[])) combinesRarity;
    mapping (uint8 => mapping(uint8 => uint8[])) combinesHeroId;
    mapping (uint8 => mapping(uint8 => uint64)) public combinesAddition; //base 1000
    mapping (address => PlayerInfo) public players; 

    event NewStaking(address player, uint256 timestamp);

    constructor(address hero, address mmc) {
        HERO = IRichHero(hero);
        MMC = IERC20(mmc);

        //五子良将 乐进 于禁 徐晃 张邰 张辽 15%
        combinesRarity[0][0] = [1, 1, 2, 2, 3];
        combinesHeroId[0][0] = [1, 3, 0, 1, 3];
        combinesAddition[0][0] = 150;
        //裸衣恶来 许褚 典韦 10%
        combinesRarity[0][1] = [3, 3];
        combinesHeroId[0][1] = [0, 1];
        combinesAddition[0][1] = 100;
        //夏侯之势 夏侯渊 夏侯惇 7%
        combinesRarity[0][2] = [2, 3];
        combinesHeroId[0][2] = [2, 2];
        combinesAddition[0][2] = 80;
        //鬼道洛神 司马懿 甄姬 3%
        combinesRarity[0][3] = [1, 2];
        combinesHeroId[0][3] = [4, 4];
        combinesAddition[0][3] = 30;

        //五虎上将 黄忠 马超 关羽 张飞 赵云 20%
        combinesRarity[1][0] = [2, 2, 3, 3, 3];
        combinesHeroId[1][0] = [0, 1, 0, 1, 3];
        combinesAddition[1][0] = 200;
        //桃园三结义 关羽 张飞 刘备 25%
        combinesRarity[1][1] = [3, 3, 4];
        combinesHeroId[1][1] = [0, 1, 0];
        combinesAddition[1][1] = 250;
        //卧龙凤雏 庞统 诸葛亮 7%
        combinesRarity[1][2] = [2, 3];
        combinesHeroId[1][2] = [4, 2];
        combinesAddition[1][2] = 70;
        //良师益友 姜维 诸葛亮 6%
        combinesRarity[1][3] = [2, 3];
        combinesHeroId[1][3] = [2, 2];
        combinesAddition[1][3] = 60;

        //东吴四英杰 周瑜 吕蒙 鲁肃 陆逊 20%
        combinesRarity[2][0] = [3, 3, 3, 3];
        combinesHeroId[2][0] = [0, 1, 2, 3];
        combinesAddition[2][0] = 200;
        //苦肉计施 黄盖 周瑜 7%
        combinesRarity[2][1] = [2, 3];
        combinesHeroId[2][1] = [0, 0];
        combinesAddition[2][1] = 70;
        //国色天香 大乔 小乔 3%
        combinesRarity[2][2] = [1, 1];
        combinesHeroId[2][2] = [4, 5];
        combinesAddition[2][2] = 30;
        //江东虎臣 韩当 周泰 黄盖 甘宁 凌统 10%
        combinesRarity[2][3] = [1, 1, 2, 2, 2];
        combinesHeroId[2][3] = [0, 3, 0, 1, 4];
        combinesAddition[2][3] = 100;

        //乱世孽缘 吕布 貂蝉 10%
        combinesRarity[3][0] = [2, 3];
        combinesHeroId[3][0] = [0, 0];
        combinesAddition[3][0] = 100;
        //悬壶济世 华佗 7%
        combinesRarity[3][1] = [2];
        combinesHeroId[3][1] = [1];
        combinesAddition[3][1] = 70;
        //酒池肉林 董卓 10%
        combinesRarity[3][2] = [4];
        combinesHeroId[3][2] = [0];
        combinesAddition[3][2] = 100;
        //黄巾之乱 张宝 张粱 张角 10%
        combinesRarity[3][3] = [1, 1, 3];
        combinesHeroId[3][3] = [0, 1, 1];
        combinesAddition[3][3] = 100;        
    }

    function staking(
        uint256[] memory tokenIds, 
        bool hasCombine, 
        uint8 combineCamp, 
        uint8 combineId, 
        uint8 difficulty
    ) public {
        require(!players[msg.sender].inStaking);
        uint256 power;
        for(uint256 i = 0; i < tokenIds.length; i++) {
            IRichHero.HeroDetail memory detail = HERO.getHero(tokenIds[i]);
            power += detail.power;
            HERO.transferFrom(msg.sender, address(this), tokenIds[i]);
        }

        if (hasCombine) {
            //todo verify
            power = power * (PCT_BASE + combinesAddition[combineCamp][combineId]) / PCT_BASE;
        }

        if (players[msg.sender].canClaimReward != 0) {
            power += players[msg.sender].canClaimReward * 10 * 100; //mmc * 10 * power
        }
        players[msg.sender].inStaking = true;
        players[msg.sender].stakingInfo.stakingDifficulty = difficulty;
        players[msg.sender].stakingInfo.stakingHeros = tokenIds;
        players[msg.sender].stakingInfo.stakingStartTime = uint64(block.timestamp);
        players[msg.sender].stakingInfo.stakingEndTime = uint64(block.timestamp + difficultyTime[difficulty] * 1 days);
        emit NewStaking(msg.sender, block.timestamp);
    }

    function calculateReward(address account) public view returns(uint256, uint256) {
        if (!players[account].inStaking) {
            return (0, 0);
        }
        uint256 day = (block.timestamp - players[account].stakingInfo.stakingStartTime) / 1 days;
        if (day > difficultyTime[players[account].stakingInfo.stakingDifficulty]) {
            day = difficultyTime[players[account].stakingInfo.stakingDifficulty];
        }
        uint256 dayReward = players[account].stakingInfo.power / difficultySpeed[players[account].stakingInfo.stakingDifficulty] / 100; //power / 100 = realpower
        uint256 reward = day * dayReward * MMC_BASE;
        return (reward, reward / 2);
    }

    function calculatePercent(address account) public view returns(uint256) {
        if (!players[account].inStaking) {
            return players[account].percent;
        }
        uint256 day = (block.timestamp - players[account].stakingInfo.stakingStartTime) / 1 days;
        if (day > difficultyTime[players[account].stakingInfo.stakingDifficulty]) {
            day = difficultyTime[players[account].stakingInfo.stakingDifficulty];
        }
        uint256 lvTotal = LV_BASE * players[account].level * players[account].level;
        uint256 percent = players[account].percent + day * players[account].stakingInfo.power / 100 * 1000 / lvTotal;
        if (percent > PCT_BASE) {
            percent = PCT_BASE;
        }
        return percent;
    }

    function claimHero() public {
        require(players[msg.sender].inStaking);
        uint256 reward;
        (uint256 fullReward, uint256 midwayReward) = calculateReward(msg.sender);
        if (block.timestamp > players[msg.sender].stakingInfo.stakingEndTime) {
            reward = fullReward;
        } else {
            reward = midwayReward;
        }
        for(uint256 i = 0; i < players[msg.sender].stakingInfo.stakingHeros.length; i++) {
            HERO.transferFrom(address(this), msg.sender, players[msg.sender].stakingInfo.stakingHeros[i]);
        }
        players[msg.sender].percent = calculatePercent(msg.sender);
        players[msg.sender].totalReward += reward;
        players[msg.sender].canClaimReward += reward;
        players[msg.sender].inStaking = false;
    }

    function claimReward() public {
        require(!players[msg.sender].inStaking);
        require(players[msg.sender].canClaimReward > 0);
        uint256 amount = players[msg.sender].canClaimReward;
        players[msg.sender].canClaimReward = 0;
        MMC.transfer(msg.sender, amount);
    }

    function occupy() public {
        require(players[msg.sender].percent == 1000);
        require(players[msg.sender].level < totalLevel);
        players[msg.sender].percent = 0;
        players[msg.sender].level += 1;
        uint256 bonus = LV_BONUS_BASE * players[msg.sender].level;
        if (players[msg.sender].level % 2 == 0) {
            bonus += 10000;
        }
        uint256 bonusMMC = bonus * MMC_BASE;
        players[msg.sender].totalReward += bonusMMC;
        players[msg.sender].canClaimReward += bonusMMC;
    }

    function getStakingHeros(address account) public view returns(uint256[] memory tokenIds) {
        if (!players[account].inStaking) {
            return tokenIds;
        }
        return players[account].stakingInfo.stakingHeros;
    }

    function isCombine(uint256[] calldata tokenIds) external view returns (bool combine, uint8 combineCamp, uint8 combineId) {
        uint256 clen = tokenIds.length;
        IRichHero.HeroDetail[] memory details = new IRichHero.HeroDetail[](clen);
        for (uint8 i = 0; i < clen; i++) {
            details[i] = HERO.getHero(tokenIds[i]);
        }
        for (uint8 camp = 0; camp < 4; camp++) {
            for (uint8 i = 0; i < 4; i++) {
                uint8 nl = uint8(combinesRarity[camp][i].length);
                if (nl > clen) {
                    continue;
                }
                for (uint8 j = 0; j < nl; j++) {
                    bool find;
                    for (uint8 k = 0; k < clen; k++) {
                        if (details[k].camp == camp && details[k].rarity == combinesRarity[camp][i][j] && details[k].heroId == combinesHeroId[camp][i][j]){
                            find = true;
                            break;
                        }
                    }
                    if (!find) {
                        break;
                    }
                    if (j+1 == nl) {
                        return (true, camp, i);
                    } 
                }
            }
        }
    }
}
